 61	 /**
 62	 * 保存字幕到缓存
 63	 * @param subtitle 字幕信息
 64	 * @param mediaId 关联的媒体ID（可选）
 65	 */
 66	 suspend fun saveSubtitle(subtitle: Subtitle, mediaId: String? = null) {
 67	 withContext(Dispatchers.IO) {
 68	 cacheMutex.withLock {
 69	 try {
 70	 // 保存到内存缓存
 71	 subtitleMemoryCache[subtitle.id] = subtitle
 72	 
 73	 // 如果有关联媒体，建立映射关系
 74	 mediaId?.let { id ->
 75	 val subtitleIds = mediaMappingCache.getOrPut(id) { mutableSetOf() }
 76	 subtitleIds.add(subtitle.id)
 77	 }
 78	 
 79	 // 持久化到SharedPreferences
 80	 persistCacheToPreferences()
 81	 
 82	 Log.d(TAG, "Subtitle cached: ${subtitle.id} for media: $mediaId")
 83	 
 84	 } catch (e: Exception) {
 85	 Log.e(TAG, "Error saving subtitle to cache", e)
 86	 }
 87	 }
 88	 }
 89	 }
 90	 
 91	 /**
 92	 * 获取媒体相关的字幕
 93	 * @param mediaId 媒体ID
 94	 * @return 字幕列表
 95	 */
 96	 fun getSubtitles(mediaId: String): List<Subtitle> {
 97	 return try {
 98	 val subtitleIds = mediaMappingCache[mediaId] ?: return emptyList()
 99	 subtitleIds.mapNotNull { subtitleMemoryCache[it] }
 100	 .filter { it.isAvailable() } // 只返回可用的字幕
 101	 } catch (e: Exception) {
 102	 Log.e(TAG, "Error getting subtitles for media: $mediaId", e)
 103	 emptyList()
 104	 }
 105	 }
 106	 
 107	 /**
 108	 * 根据ID获取字幕
 109	 * @param subtitleId 字幕ID
 110	 * @return 字幕信息，如果不存在返回null
 111	 */
 112	 fun getSubtitle(subtitleId: String): Subtitle? {
 113	 return subtitleMemoryCache[subtitleId]
 114	 }
 115	 
 116	 /**
 117	 * 搜索字幕
 118	 * @param query 搜索关键词
 119	 * @param language 语言过滤（可选）
 120	 * @return 匹配的字幕列表
 121	 */
 122	 fun searchSubtitles(query: String, language: String? = null): List<Subtitle> {
 123	 return try {
 124	 val lowerQuery = query.lowercase()
 125	 subtitleMemoryCache.values.filter { subtitle ->
 126	 val matchesQuery = subtitle.title.lowercase().contains(lowerQuery) ||
 127	 subtitle.languageName.lowercase().contains(lowerQuery)
 128	 
 129	 val matchesLanguage = language?.let { lang ->
 130	 subtitle.language.equals(lang, ignoreCase = true) ||
 131	 subtitle.language.startsWith(lang, ignoreCase = true)
 132	 } ?: true
 133	 
 134	 matchesQuery && matchesLanguage && subtitle.isAvailable()
 135	 }.sortedByDescending { it.rating }
 136	 } catch (e: Exception) {
 137	 Log.e(TAG, "Error searching subtitles", e)
 138	 emptyList()
 139	 }
 140	 }
 141	 
 142	 /**
 143	 * 删除字幕
 144	 * @param subtitleId 字幕ID
 145	 */
 146	 suspend fun removeSubtitle(subtitleId: String) {
 147	 withContext(Dispatchers.IO) {
 148	 cacheMutex.withLock {
 149	 try {
 150	 val subtitle = subtitleMemoryCache[subtitleId]
 151	 
 152	 // 删除本地文件
 153	 subtitle?.let { sub ->
 154	 if (sub.isDownloaded && sub.localPath.isNotEmpty()) {
 155	 val file = File(sub.localPath)
 156	 if (file.exists() && file.delete()) {
 157	 Log.d(TAG, "Deleted subtitle file: ${sub.localPath}")
 158	 }
 159	 }
 160	 }
 161	 
 162	 // 从内存缓存移除
 163	 subtitleMemoryCache.remove(subtitleId)
 164	 
 165	 // 从媒体映射中移除
 166	 mediaMappingCache.values.forEach { subtitleIds ->
 167	 subtitleIds.remove(subtitleId)
 168	 }
 169	 
 170	 // 持久化更改
 171	 persistCacheToPreferences()
 172	 
 173	 Log.d(TAG, "Subtitle removed from cache: $subtitleId")
 174	 
 175	 } catch (e: Exception) {
 176	 Log.e(TAG, "Error removing subtitle from cache", e)
 177	 }
 178	 }
 179	 }
 180	 }
 181	 
 182	 /**
 183	 * 清理过期字幕
 184	 * @param expireDays 过期天数
 185	 */
 186	 suspend fun cleanExpiredSubtitles(expireDays: Int = 30) {
 187	 withContext(Dispatchers.IO) {
 188	 cacheMutex.withLock {
 189	 try {
 190	 val expireTime = System.currentTimeMillis() - (expireDays * 24 * 60 * 60 * 1000L)
 191	 val expiredSubtitleIds = mutableListOf<String>()
 192	 
 193	 subtitleMemoryCache.values.forEach { subtitle ->
 194	 if (subtitle.uploadDate > 0 && subtitle.uploadDate < expireTime) {
 195	 expiredSubtitleIds.add(subtitle.id)
 196	 }
 197	 }
 198	 
 199	 // 删除过期字幕
 200	 for (subtitleId in expiredSubtitleIds) {
 201	 try {
 202	 val subtitle = subtitleMemoryCache[subtitleId]
 203	 
 204	 // 删除本地文件
 205	 subtitle?.let { sub ->
 206	 if (sub.isDownloaded && sub.localPath.isNotEmpty()) {
 207	 val file = File(sub.localPath)
 208	 if (file.exists() && file.delete()) {
 209	 Log.d(TAG, "Deleted subtitle file: ${sub.localPath}")
 210	 }
 211	 }
 212	 }
 213	 
 214	 // 从内存缓存移除
 215	 subtitleMemoryCache.remove(subtitleId)
 216	 
 217	 // 从媒体映射中移除
 218	 mediaMappingCache.values.forEach { subtitleIds ->
 219	 subtitleIds.remove(subtitleId)
 220	 }
 221	 } catch (e: Exception) {
 222	 Log.e(TAG, "Error removing expired subtitle: $subtitleId", e)
 223	 }
 224	 }
 225	 
 226	 // 持久化更改
 227	 persistCacheToPreferences()
 228	 
 229	 // 清理孤立的字幕文件
 230	 cleanOrphanedFiles()
 231	 
 232	 // 更新最后清理时间
 233	 preferences.edit()
 234	 .putLong(KEY_LAST_CLEANUP, System.currentTimeMillis())
 235	 .apply()
 236	 
 237	 Log.d(TAG, "Cleaned ${expiredSubtitleIds.size} expired subtitles")
 238	 
 239	 } catch (e: Exception) {
 240	 Log.e(TAG, "Error cleaning expired subtitles", e)
 241	 }
 242	 }
 243	 }
 244	 }
 245	 
 246	 /**
 247	 * 清理缓存以释放空间
 248	 * @param targetSize 目标大小（字节）
 249	 */
 250	 suspend fun cleanupToSize(targetSize: Long = MAX_CACHE_SIZE) {
 251	 withContext(Dispatchers.IO) {
 252	 cacheMutex.withLock {
 253	 try {
 254	 val currentSize = getCacheSize()
 255	 if (currentSize <= targetSize) {
 256	 return@withLock
 257	 }
 258	 
 259	 Log.d(TAG, "Cache size: $currentSize bytes, target: $targetSize bytes")
 260	 
 261	 // 按最后访问时间排序，删除最老的文件
 262	 val subtitleFiles = subtitleDir.listFiles()?.sortedBy { it.lastModified() } ?: return@withLock
 263	 var freedSize = 0L
 264	 
 265	 for (file in subtitleFiles) {
 266	 if (currentSize - freedSize <= targetSize) {
 267	 break
 268	 }
 269	 
 270	 val fileSize = file.length()
 271	 if (file.delete()) {
 272	 freedSize += fileSize
 273	 
 274	 // 从缓存中移除对应的字幕记录（非异步方式）
 275	 val subtitleToRemove = subtitleMemoryCache.values.find { 
 276	 it.localPath == file.absolutePath 
 277	 }
 278	 subtitleToRemove?.let { subtitle ->
 279	 // 直接从内存缓存移除，避免suspend函数调用
 280	 subtitleMemoryCache.remove(subtitle.id)
 281	 
 282	 // 从媒体映射中移除
 283	 mediaMappingCache.values.forEach { subtitleIds ->
 284	 subtitleIds.remove(subtitle.id)
 285	 }
 286	 }
 287	 }
 288	 }
 289	 
 290	 Log.d(TAG, "Freed $freedSize bytes from cache")
 291	 
 292	 } catch (e: Exception) {
 293	 Log.e(TAG, "Error cleaning up cache", e)
 294	 }
 295	 }
 296	 }
 297	 }
