 299	 /**
 300	 * 获取缓存统计信息
 301	 */
 302	 fun getCacheStats(): CacheStats {
 303	 return try {
 304	 val totalSubtitles = subtitleMemoryCache.size
 305	 val downloadedSubtitles = subtitleMemoryCache.values.count { it.isDownloaded }
 306	 val cacheSize = getCacheSize()
 307	 val lastCleanup = preferences.getLong(KEY_LAST_CLEANUP, 0L)
 308	 
 309	 CacheStats(
 310	 totalSubtitles = totalSubtitles,
 311	 downloadedSubtitles = downloadedSubtitles,
 312	 cacheSize = cacheSize,
 313	 lastCleanup = lastCleanup
 314	 )
 315	 } catch (e: Exception) {
 316	 Log.e(TAG, "Error getting cache stats", e)
 317	 CacheStats()
 318	 }
 319	 }
