 321	 /**
 322	 * 清空所有缓存
 323	 */
 324	 suspend fun clearAllCache() {
 325	 withContext(Dispatchers.IO) {
 326	 cacheMutex.withLock {
 327	 try {
 328	 // 删除所有字幕文件
 329	 subtitleDir.listFiles()?.forEach { file ->
 330	 file.delete()
 331	 }
 332	 
 333	 // 清空内存缓存
 334	 subtitleMemoryCache.clear()
 335	 mediaMappingCache.clear()
 336	 
 337	 // 清空持久化数据
 338	 preferences.edit().clear().apply()
 339	 
 340	 Log.d(TAG, "All cache cleared")
 341	 
 342	 } catch (e: Exception) {
 343	 Log.e(TAG, "Error clearing cache", e)
 344	 }
 345	 }
 346	 }
 347	 }
