 349	 // 私有辅助方法
 350	 
 351	 private fun loadCacheFromPreferences() {
 352	 try {
 353	 // 加载字幕数据
 354	 val subtitlesJson = preferences.getString(KEY_SUBTITLES, "")
 355	 if (!subtitlesJson.isNullOrEmpty()) {
 356	 val type = object : TypeToken<Map<String, Subtitle>>() {}.type
 357	 val subtitles: Map<String, Subtitle> = gson.fromJson(subtitlesJson, type)
 358	 subtitleMemoryCache.putAll(subtitles)
 359	 }
 360	 
 361	 // 加载媒体映射
 362	 val mappingJson = preferences.getString(KEY_MEDIA_MAPPING, "")
 363	 if (!mappingJson.isNullOrEmpty()) {
 364	 val type = object : TypeToken<Map<String, Set<String>>>() {}.type
 365	 val mapping: Map<String, Set<String>> = gson.fromJson(mappingJson, type)
 366	 mapping.forEach { (mediaId, subtitleIds) ->
 367	 mediaMappingCache[mediaId] = subtitleIds.toMutableSet()
 368	 }
 369	 }
 370	 
 371	 Log.d(TAG, "Loaded ${subtitleMemoryCache.size} subtitles from cache")
 372	 
 373	 } catch (e: Exception) {
 374	 Log.e(TAG, "Error loading cache from preferences", e)
 375	 }
 376	 }
 377	 
 378	 private fun persistCacheToPreferences() {
 379	 try {
 380	 val editor = preferences.edit()
 381	 
 382	 // 保存字幕数据
 383	 val subtitlesJson = gson.toJson(subtitleMemoryCache)
 384	 editor.putString(KEY_SUBTITLES, subtitlesJson)
 385	 
 386	 // 保存媒体映射
 387	 val mappingJson = gson.toJson(mediaMappingCache)
 388	 editor.putString(KEY_MEDIA_MAPPING, mappingJson)
 389	 
 390	 editor.apply()
 391	 
 392	 } catch (e: Exception) {
 393	 Log.e(TAG, "Error persisting cache to preferences", e)
 394	 }
 395	 }
 396	 
 397	 private fun getCacheSize(): Long {
 398	 return try {
 399	 subtitleDir.listFiles()?.sumOf { it.length() } ?: 0L
 400	 } catch (e: Exception) {
 401	 0L
 402	 }
 403	 }
 404	 
 405	 private fun cleanOrphanedFiles() {
 406	 try {
 407	 val cachedPaths = subtitleMemoryCache.values
 408	 .mapNotNull { it.localPath.takeIf { path -> path.isNotEmpty() } }
 409	 .toSet()
 410	 
 411	 subtitleDir.listFiles()?.forEach { file ->
 412	 if (file.absolutePath !in cachedPaths) {
 413	 if (file.delete()) {
 414	 Log.d(TAG, "Deleted orphaned file: ${file.name}")
 415	 }
 416	 }
 417	 }
 418	 } catch (e: Exception) {
 419	 Log.w(TAG, "Error cleaning orphaned files: ${e.message}")
 420	 }
 421	 }
 422	 
 423	 private fun checkAndCleanupIfNeeded() {
 424	 try {
 425	 val lastCleanup = preferences.getLong(KEY_LAST_CLEANUP, 0L)
 426	 val daysSinceCleanup = (System.currentTimeMillis() - lastCleanup) / (24 * 60 * 60 * 1000L)
 427	 
 428	 if (daysSinceCleanup >= CLEANUP_INTERVAL_DAYS) {
 429	 // 异步执行清理，但不等待结果以避免suspend函数调用错误
 430	 kotlinx.coroutines.GlobalScope.launch {
 431	 try {
 432	 cleanExpiredSubtitles()
 433	 cleanupToSize()
 434	 } catch (e: Exception) {
 435	 Log.w(TAG, "Error during background cleanup: ${e.message}")
 436	 }
 437	 }
 438	 }
 439	 } catch (e: Exception) {
 440	 Log.w(TAG, "Error checking cleanup: ${e.message}")
 441	 }
 442	 }
 443	}
