 445	/**
 446	 * 缓存统计信息
 447	 */
 448	data class CacheStats(
 449	 val totalSubtitles: Int = 0,
 450	 val downloadedSubtitles: Int = 0,
 451	 val cacheSize: Long = 0L,
 452	 val lastCleanup: Long = 0L
 453	) {
 454	 val cacheSizeMB: Float
 455	 get() = cacheSize / (1024f * 1024f)
 456	 
 457	 val daysSinceCleanup: Long
 458	 get() = if (lastCleanup > 0) {
 459	 (System.currentTimeMillis() - lastCleanup) / (24 * 60 * 60 * 1000L)
 460	 } else {
 461	 -1L
 462	 }
