 45	 // 禁用ProxyCache服务器
 46	 val proxyCacheManagerClass = Class.forName("com.danikula.videocache.ProxyCacheManager")
 47	 val isInstanceMethod = proxyCacheManagerClass.getMethod("isInstance")
 48	 val isInstance = isInstanceMethod.invoke(null) as Boolean
 49	 
 50	 if (isInstance) {
 51	 val shutdownMethod = proxyCacheManagerClass.getMethod("shutdown")
 52	 shutdownMethod.invoke(null)
 53	 Log.i("TVPlayerApplication", "ProxyCache disabled for WebDAV support")
 54	 }
 55	 } catch (e: Exception) {
 56	 Log.w("TVPlayerApplication", "Could not disable ProxyCache, using alternative method: ${e.message}")
