 315	 * 转换TMDB电视剧数据为MediaItem
 316	 */
 317	 private fun convertTmdbTVToMediaItem(
 318	 tvShow: TmdbTVShow,
 319	 filePath: String,
 320	 seasonNumber: Int?,
 321	 episodeNumber: Int?,
 322	 episodeTitle: String?,
 323	 episodeOverview: String?,
 324	 episodeRuntime: Long,
 325	 fileSize: Long
 326	 ): MediaItem {
 327	 val releaseDate = try {
 328	 tvShow.firstAirDate?.let { dateFormat.parse(it) }
 329	 } catch (e: Exception) {
 330	 null
 331	 }
 332	
 333	 return MediaItem(
 334	 id = "tv_${tvShow.id}_s${seasonNumber}_e${episodeNumber}",
 335	 title = episodeTitle ?: "第${episodeNumber}集",
 336	 originalTitle = tvShow.originalName,
 337	 overview = tvShow.overview,
 338	 posterPath = tvShow.posterPath?.let { "${TmdbApiService.IMAGE_BASE_URL}${TmdbApiService.POSTER_SIZE_W500}$it" },
 339	 backdropPath = tvShow.backdropPath?.let { "${TmdbApiService.IMAGE_BASE_URL}${TmdbApiService.BACKDROP_SIZE_W1280}$it" },
 340	 releaseDate = releaseDate,
 341	 rating = tvShow.voteAverage,
 342	 duration = episodeRuntime,
 343	 mediaType = MediaType.TV_EPISODE,
 344	 filePath = filePath,
 345	 fileSize = fileSize,
 346	 seasonNumber = seasonNumber,
 347	 episodeNumber = episodeNumber,
 348	 seriesId = "tv_${tvShow.id}",
 349	 seriesTitle = tvShow.name,
 350	 genre = tvShow.genres?.map { it.name } ?: emptyList()
 351	 )
 352	 }
