 396	 
 397	 /**
 398	 * 生成匹配原因
 399	 */
 400	 private fun generateMatchReasons(
 401	 mediaItem: MediaItem,
 402	 subtitle: Subtitle,
 403	 similarity: Float
 404	 ): List<MatchReason> {
 405	 val reasons = mutableListOf<MatchReason>()
 406	 
 407	 if (similarity >= EXCELLENT_MATCH_THRESHOLD) {
 408	 reasons.add(MatchReason.EXCELLENT_MATCH)
 409	 } else if (similarity >= GOOD_MATCH_THRESHOLD) {
 410	 reasons.add(MatchReason.GOOD_MATCH)
 411	 }
 412	 
 413	 // 具体匹配原因
 414	 if (calculateTextSimilarity(mediaItem.title, subtitle.title) > 0.8f) {
 415	 reasons.add(MatchReason.TITLE_MATCH)
 416	 }
 417	 
 418	 val mediaYear = getYearFromMediaItem(mediaItem)
 419	 if (mediaYear > 0 && subtitle.metadata["year"]?.toIntOrNull() == mediaYear) {
 420	 reasons.add(MatchReason.YEAR_MATCH)
 421	 }
 422	 
 423	 if ((mediaItem.mediaType == MediaType.TV_EPISODE || mediaItem.mediaType == MediaType.TV_SERIES) && calculateEpisodeScore(mediaItem, subtitle) == 1.0f) {
 424	 reasons.add(MatchReason.EPISODE_MATCH)
 425	 }
 426	 
 427	 if (subtitle.rating > 8.0f) {
 428	 reasons.add(MatchReason.HIGH_RATING)
 429	 }
 430	 
 431	 if (subtitle.downloadCount > 1000) {
 432	 reasons.add(MatchReason.POPULAR)
 433	 }
 434	 
 435	 return reasons
 436	 }
