 45	 /**
 46	 * 获取格式化的文件大小
 47	 */
 48	 fun getFormattedSize(): String {
 49	 if (isDirectory) return ""
 50	 
 51	 return when {
 52	 size < 1024 -> "${size}B"
 53	 size < 1024 * 1024 -> "${size / 1024}KB"
 54	 size < 1024 * 1024 * 1024 -> "${size / (1024 * 1024)}MB"
 55	 else -> "${size / (1024 * 1024 * 1024)}GB"
 56	 }
 57	 }
