 30	 "mp4", "mkv", "avi", "mov", "wmv", "flv", "webm", "m4v", "3gp", "ts", "m2ts"
 31	 )
 32	
 33	 // 电视剧季集识别正则
 34	 private val TV_SEASON_EPISODE_PATTERNS = listOf(
 35	 Pattern.compile("S(\\d+)E(\\d+)", Pattern.CASE_INSENSITIVE),
 36	 Pattern.compile("Season\\s*(\\d+).*Episode\\s*(\\d+)", Pattern.CASE_INSENSITIVE),
 37	 Pattern.compile("(\\d+)x(\\d+)", Pattern.CASE_INSENSITIVE)
 38	 )
