 47	 /**
 48	 * 扫描进度回调
 49	 */
 50	 interface ScanProgressCallback {
 51	 fun onProgress(current: Int, total: Int, currentFile: String)
 52	 fun onComplete(scannedItems: List<MediaItem>)
 53	 fun onError(error: String)
 54	 }
 55	
 56	 /**
 57	 * 扫描WebDAV目录
 58	 * @param path 要扫描的路径
 59	 * @param recursive 是否递归扫描子目录
 60	 * @param callback 进度回调
 61	 */
 62	 suspend fun scanDirectory(
 63	 path: String,
 64	 recursive: Boolean = true,
 65	 callback: ScanProgressCallback? = null,
 66	 modeHint: ModeHint? = null
 67	 ): List<MediaItem> {
 68	 return withContext(Dispatchers.IO) {
 69	 val scannedItems = mutableListOf<MediaItem>()
 70	
 71	 try {
 72	 Log.d(TAG, "Starting scan of directory: $path")
 73	
 74	 val files: List<WebDAVFile> = if (recursive) {
 75	 getAllFilesRecursively(path)
 76	 } else {
 77	 getFilesInDirectory(path)
 78	 }
 79	
 80	 val videoFiles = files.filter { !it.isDirectory && isVideoFile(it.name) }
 81	
 82	 val total = videoFiles.size.coerceAtLeast(1)
 83	 var current = 0
 84	
 85	 for (file in videoFiles) {
 86	 current += 1
 87	 callback?.onProgress(current, total, file.path)
 88	
 89	 val mediaItem = scrapeMediaFile(file, modeHint)
 90	 if (mediaItem != null) {
 91	 scannedItems.add(mediaItem)
 92	 }
 93	 }
 94	
 95	 callback?.onComplete(scannedItems)
 96	 Log.d(TAG, "Scan completed. Found ${scannedItems.size} media items")
 97	
 98	 } catch (e: Exception) {
 99	 Log.e(TAG, "Error during scan", e)
 100	 callback?.onError(e.message ?: "Unknown error")
 101	 }
 102	
 103	 scannedItems
 104	 }
 105	 }
...
 138	
 139	 /**
 140	 * 刮削单个媒体文件
 141	 */
 142	 private suspend fun scrapeMediaFile(file: WebDAVFile, modeHint: ModeHint? = null): MediaItem? {
 143	 val name = file.name
 144	 val path = file.path
 145	 val fileSize = file.size
 146	 
 147	 // 临时简化实现，直接创建基本的MediaItem
 148	 return try {
 149	 MediaItem(
 150	 id = path.hashCode().toString(),
 151	 title = name.substringBeforeLast('.'),
 152	 originalTitle = null,
 153	 overview = null,
 154	 posterPath = null,
 155	 backdropPath = null,
 156	 releaseDate = null,
 157	 rating = 0f,
 158	 duration = 0L,
 159	 mediaType = when (modeHint) {
 160	 ModeHint.MOVIE -> com.tvplayer.webdav.data.model.MediaType.MOVIE
 161	 ModeHint.TV -> com.tvplayer.webdav.data.model.MediaType.TV_EPISODE
 162	 null -> {
 163	 // 简单判断：如果文件名包含S\d+E\d+模式，则认为是电视剧
 164	 if (TV_SEASON_EPISODE_PATTERNS.any { it.matcher(name).find() }) {
 165	 com.tvplayer.webdav.data.model.MediaType.TV_EPISODE
 166	 } else {
 167	 com.tvplayer.webdav.data.model.MediaType.MOVIE
 168	 }
 169	 }
 170	 },
 171	 filePath = path,
 172	 fileSize = fileSize,
 173	 lastModified = null
 174	 )
 175	 } catch (e: Exception) {
 176	 Log.e(TAG, "Error creating MediaItem for file: $path", e)
 177	 null
 178	 }
 179	 }
 180	
 181	 /**
 182	 * 检查是否为视频文件
 183	 */
 184	 private fun isVideoFile(fileName: String): Boolean {
 185	 val extension = fileName.substringAfterLast('.', "").lowercase()
 186	 return extension in VIDEO_EXTENSIONS
 187	 }
 188	}
