 9	 * 支持电影、电视剧、单集等不同类型
 10	 */
 11	@Parcelize
 12	data class MediaItem(
 13	 val id: String,
 14	 val title: String,
 15	 val originalTitle: String? = null,
 16	 val overview: String? = null,
 17	 val posterPath: String? = null,
 18	 val backdropPath: String? = null,
 19	 val releaseDate: Date? = null,
 20	 val rating: Float = 0f,
 21	 val duration: Long = 0L, // 时长（秒）
 22	 val mediaType: MediaType,
 23	 val filePath: String,
 24	 val fileSize: Long = 0L,
 25	 val lastModified: Date? = null,
 26	 
 27	 // 电视剧相关
 28	 val seasonNumber: Int? = null,
 29	 val episodeNumber: Int? = null,
 30	 val seriesId: String? = null,
 31	 val seriesTitle: String? = null,
 32	 
 33	 // 播放相关
 34	 val watchedProgress: Float = 0f, // 观看进度 0-1
 35	 val isWatched: Boolean = false,
 36	 val lastWatchedTime: Date? = null,
 37	 
 38	 // 收藏和标签
 39	 val isFavorite: Boolean = false,
 40	 val tags: List<String> = emptyList(),
 41	 val genre: List<String> = emptyList()
 42	) : Parcelable {
 43	 
 44	 /**
 45	 * 获取显示标题
 46	 */
 47	 fun getDisplayTitle(): String {
 48	 fun deriveNameFromFile(): String {
 49	 val base = filePath.substringAfterLast('/')
 50	 .substringBeforeLast('.')
 51	 .replace(Regex("[\\[\\(（【].*?[\\]\\)）】]"), " ")
 52	 .replace(Regex("[._-]+"), " ")
 53	 .replace(Regex("\\s+"), " ")
 54	 .trim()
 55	 return if (base.isNotBlank()) base else "未命名"
 56	 }
 57	 return when (mediaType) {
 58	 MediaType.TV_EPISODE -> {
 59	 val composite = if (seasonNumber != null && episodeNumber != null && !seriesTitle.isNullOrBlank()) {
 60	 "$seriesTitle S${seasonNumber.toString().padStart(2, '0')}E${episodeNumber.toString().padStart(2, '0')}"
 61	 } else title
 62	 (composite?.takeIf { it.isNotBlank() } ?: seriesTitle ?: deriveNameFromFile())
 63	 }
 64	 else -> (title.takeIf { it.isNotBlank() } ?: originalTitle?.takeIf { it.isNotBlank() } ?: deriveNameFromFile())
 65	 }
 66	 }
...
 96	 
 97	 /**
 98	 * 获取观看进度百分比
 99	 */
 100	 fun getWatchedPercentage(): Int {
 101	 return (watchedProgress * 100).toInt()
 102	 }
 103	 
 104	 /**
 105	 * 是否为新内容（最近添加）
 106	 */
 107	 fun isNew(): Boolean {
 108	 val now = System.currentTimeMillis()
 109	 val weekAgo = now - 7 * 24 * 60 * 60 * 1000L
 110	 return lastModified?.time ?: 0L > weekAgo
 111	 }
 112	}
