 354	 /**
 355	 * 确保返回中文（zh-CN）字段：title/name/overview
 356	 * 若 getDetails 已经是 zh-CN，则直接返回；否则从 translations 中找 zh-CN 数据覆盖
 357	 */
 358	 private suspend fun ensureChineseMovie(movieId: Int, details: TmdbMovie): TmdbMovie {
 359	 return withContext(Dispatchers.IO) {
 360	 try {
 361	 val tr = apiService.getMovieTranslations(movieId, API_KEY)
 362	 val cn = tr.body()?.translations?.firstOrNull { it.iso_639_1?.equals("zh", true) == true }
 363	 if (cn?.data != null) {
 364	 val zhTitle = cn.data.title
 365	 val zhOverview = cn.data.overview
 366	 details.copy(
 367	 title = if (!zhTitle.isNullOrBlank()) zhTitle else details.title,
 368	 overview = if (!zhOverview.isNullOrBlank()) zhOverview else details.overview
 369	 )
 370	 } else details
 371	 } catch (e: Exception) {
 372	 Log.w(TAG, "ensureChineseMovie failed", e)
 373	 details
 374	 }
 375	 }
 376	 }
