 357	 private fun loadCacheFromPreferences() {
 358	 try {
 359	 // 加载字幕数据
 360	 val subtitlesJson = preferences.getString(KEY_SUBTITLES, "")
 361	 if (!subtitlesJson.isNullOrEmpty()) {
 362	 val type = object : TypeToken<Map<String, Subtitle>>() {}.type
 363	 val subtitles: Map<String, Subtitle> = gson.fromJson(subtitlesJson, type)
 364	 subtitleMemoryCache.putAll(subtitles)
 365	 }
 366	 
 367	 // 加载媒体映射
 368	 val mappingJson = preferences.getString(KEY_MEDIA_MAPPING, "")
 369	 if (!mappingJson.isNullOrEmpty()) {
 370	 val type = object : TypeToken<Map<String, Set<String>>>() {}.type
 371	 val mapping: Map<String, Set<String>> = gson.fromJson(mappingJson, type)
 372	 mapping.forEach { (mediaId, subtitleIds) ->
 373	 mediaMappingCache[mediaId] = subtitleIds.toMutableSet()
 374	 }
 375	 }
 376	 
 377	 Log.d(TAG, "Loaded ${subtitleMemoryCache.size} subtitles from cache")
 378	 
 379	 } catch (e: Exception) {
 380	 Log.e(TAG, "Error loading cache from preferences", e)
 381	 }
 382	 }
 383	 
 384	 private fun persistCacheToPreferences() {
 385	 try {
 386	 val editor = preferences.edit()
 387	 
 388	 // 保存字幕数据
 389	 val subtitlesJson = gson.toJson(subtitleMemoryCache)
 390	 editor.putString(KEY_SUBTITLES, subtitlesJson)
 391	 
 392	 // 保存媒体映射
 393	 val mappingJson = gson.toJson(mediaMappingCache)
 394	 editor.putString(KEY_MEDIA_MAPPING, mappingJson)
 395	 
 396	 editor.apply()
 397	 
 398	 } catch (e: Exception) {
 399	 Log.e(TAG, "Error persisting cache to preferences", e)
 400	 }
 401	 }
