 64	 /**
 65	 * 加载主界面数据
 66	 */
 67	 fun loadHomeData() {
 68	 viewModelScope.launch {
 69	 _isLoading.value = true
 70	 try {
 71	 // 首页优先依赖扫描页写入的缓存，不再扫描根目录
 72	 val server = serverStorage.getServer()
 73	 if (server == null) {
 74	 // 无服务器时，展示示例数据
 75	 loadSampleData()
 76	 _error.value = null
 77	 } else {
 78	 // 已配置服务器：等待/使用缓存，避免与扫描页冲突
 79	 _error.value = null
 80	 }
 81	 } catch (e: Exception) {
 82	 _error.value = e.message
 83	 } finally {
 84	 _isLoading.value = false
 85	 }
 86	 }
 87	 }
