 48	 fun deriveNameFromFile(): String {
 49	 val base = filePath.substringAfterLast('/')
 50	 .substringBeforeLast('.')
 51	 .replace(Regex("[\\[\\(（【].*?[\\]\\)）】]"), " ")
 52	 .replace(Regex("[._-]+"), " ")
 53	 .replace(Regex("\\s+"), " ")
 54	 .trim()
 55	 return if (base.isNotBlank()) base else "未命名"
 56	 }
