 412	 /**
 413	 * 获取电影详细信息（包括演员）
 414	 * @param movieId 电影ID
 415	 * @return 电影详细信息
 416	 */
 417	 suspend fun getMovieDetailsWithCast(movieId: Int): Pair<TmdbMovie, List<TmdbCast>>? {
 418	 return withContext(Dispatchers.IO) {
 419	 try {
 420	 // 获取电影详情
 421	 val detailsResponse = apiService.getMovieDetails(movieId, API_KEY)
 422	 val movieDetails = detailsResponse.body()
 423	 
 424	 if (movieDetails != null) {
 425	 // 获取演员信息
 426	 val castResponse = apiService.getMovieCredits(movieId, API_KEY)
 427	 val cast = castResponse.body()?.cast ?: emptyList()
 428	 
 429	 // 确保中文标题/概述
 430	 val finalizedMovie = ensureChineseMovie(movieId, movieDetails)
 431	 
 432	 Pair(finalizedMovie, cast)
 433	 } else {
 434	 null
 435	 }
 436	 } catch (e: Exception) {
 437	 Log.e(TAG, "Error getting movie details with cast: $movieId", e)
 438	 null
 439	 }
 440	 }
 441	 }
 442	
 443	 /**
 444	 * 获取电视剧详细信息（包括演员）
 445	 * @param tvId 电视剧ID
 446	 * @return 电视剧详细信息和演员列表
 447	 */
 448	 suspend fun getTVShowDetailsWithCast(tvId: Int): Pair<TmdbTVShow, List<TmdbCast>>? {
 449	 return withContext(Dispatchers.IO) {
 450	 try {
 451	 // 获取电视剧详情
 452	 val detailsResponse = apiService.getTVShowDetails(tvId, API_KEY)
 453	 val tvDetails = detailsResponse.body()
 454	
 455	 if (tvDetails != null) {
 456	 // 获取演员信息
 457	 val castResponse = apiService.getTVCredits(tvId, API_KEY)
 458	 val cast = castResponse.body()?.cast ?: emptyList()
 459	
 460	 // 确保中文标题/概述
 461	 val finalizedTV = ensureChineseTV(tvId, tvDetails)
 462	
 463	 Pair(finalizedTV, cast)
 464	 } else {
 465	 null
 466	 }
 467	 } catch (e: Exception) {
 468	 Log.e(TAG, "Error getting TV show details with cast: $tvId", e)
 469	 null
 470	 }
 471	 }
 472	 }
