 75	 override suspend fun searchSubtitles(request: SubtitleSearchRequest): Result<List<Subtitle>> {
 76	 return try {
 77	 Log.d(TAG, "Searching subtitles for: ${request.title}")
 78	 
 79	 ensureAuthenticated()
 80	 
 81	 val searchParams = buildSearchParams(request)
 82	 val searchUrl = "$BASE_URL$ENDPOINT_SEARCH?$searchParams"
 83	 
 84	 val httpRequest = Request.Builder()
 85	 .url(searchUrl)
 86	 .addHeader("Authorization", "Bearer $authToken")
 87	 .addHeader("User-Agent", USER_AGENT)
 88	 .addHeader("Accept", "application/json")
 89	 .build()
 90	 
 91	 val response = client.newCall(httpRequest).execute()
 92	 updateApiLimits(response)
 93	 
 94	 if (!response.isSuccessful) {
 95	 throw IOException("Search failed: ${response.code} ${response.message}")
 96	 }
 97	 
 98	 val responseBody = response.body?.string() ?: ""
 99	 val subtitles = parseSearchResponse(responseBody)
 100	 
 101	 Log.d(TAG, "Found ${subtitles.size} subtitles for ${request.title}")
 102	 Result.success(subtitles)
 103	 
 104	 } catch (e: Exception) {
 105	 Log.e(TAG, "Error searching subtitles", e)
 106	 Result.failure(e)
 107	 }
 108	 }
