 101	 SUBSCENE("Subscene", "https://subscene.com"),
 102	 YIFYSUBTITLES("YifySubtitles", "https://yifysubtitles.org"),
 103	 SUBDL("SubDL", "https://subdl.com"),
 104	 LOCAL("本地字幕", ""),
 105	 MANUAL("手动导入", ""),
 106	 UNKNOWN("未知来源", "");
 107	 
 108	 val isOnline: Boolean
 109	 get() = baseUrl.isNotEmpty()
 110	}
