 428	 /**
 429	 * 从标题提取季集信息
 430	 */
 431	 private fun extractSeasonEpisodeFromTitle(title: String): Pair<Int, Int> {
 432	 val seRegex = """[Ss](\d+)[Ee](\d+)""".toRegex()
 433	 val match = seRegex.find(title)
 434	 return if (match != null) {
 435	 val season = match.groupValues[1].toIntOrNull() ?: 0
 436	 val episode = match.groupValues[2].toIntOrNull() ?: 0
 437	 Pair(season, episode)
 438	 } else {
 439	 Pair(0, 0)
 440	 }
 441	 }
