 val success = when (subtitle.format) {
 SubtitleFormat.SRT -> loadSrtSubtitle(subtitleFile)
 SubtitleFormat.ASS, SubtitleFormat.SSA -> loadAssSubtitle(subtitleFile)
 // 其他格式...
 }
