```kotlin
fun searchAndSelectSubtitle() {
 activityScope.launch {
 try {
 val mediaItem = currentMediaItem ?: return@launch
 val searchRequest = SubtitleSearchRequest.fromMediaItem(mediaItem, subtitleConfig)
 val searchResult = subtitleManager.searchSubtitles(searchRequest, subtitleConfig)

 if (searchResult.hasResults) {
 // 显示字幕选择对话框
 showSubtitleSelectionDialog(searchResult.subtitles)
 } else {
 // 显示无字幕提示
 showNoSubtitlesMessage()
 }
 } catch (e: Exception) {
 Log.e("PlayerActivity", "Error searching subtitles", e)
 }
 }
}

private fun showSubtitleSelectionDialog(subtitles: List<Subtitle>) {
 val dialog = SubtitleSelectionDialog(
 context = this,
 onSubtitleSelected = { subtitle ->
 downloadAndLoadSubtitle(subtitle)
 },
 onSearchSubtitles = { searchAndSelectSubtitle() },
 onConfigSubtitles = { showSubtitleConfigDialog() }
 )
 dialog.updateSubtitles(subtitles)
 dialog.show()
}
```

