 425	 private fun setupExoPlayerSubtitle(context: Context, uri: Uri, mimeType: String): Boolean {
 426	 return try {
 427	 // 创建字幕媒体源
 428	 val dataSourceFactory: DataSource.Factory = DefaultDataSourceFactory(context, "SubtitlePlayer")
 429	 
 430	 val subtitleMediaItem = MediaItem.SubtitleConfiguration.Builder(uri)
 431	 .setMimeType(mimeType)
 432	 .setLanguage("und") // 未定义语言
 433	 .setSelectionFlags(com.google.android.exoplayer2.C.SELECTION_FLAG_DEFAULT)
 434	 .build()
 435	 
 436	 val subtitleSource = SingleSampleMediaSource.Factory(dataSourceFactory)
 437	 .createMediaSource(subtitleMediaItem, com.google.android.exoplayer2.C.TIME_UNSET)
 438	 
 439	 // 注意：这里需要根据GSYVideoPlayer的具体实现来调整
 440	 // GSYVideoPlayer可能需要特殊的方式来设置字幕源
 441	 Log.d(TAG, "ExoPlayer subtitle source created for: $uri")
 442	 
 443	 true
 444	 } catch (e: Exception) {
 445	 Log.e(TAG, "Error setting up ExoPlayer subtitle", e)
 446	 false
 447	 }
 448	 }
 449	}
