 19	/**
 20	 * 字幕缓存管理器
 21	 * 负责字幕信息的本地存储、检索和清理
 22	 */
 23	@Singleton
 24	class SubtitleCache @Inject constructor(
 25	 @ApplicationContext private val context: Context,
 26	 private val gson: Gson
 27	) {
 28	 
 29	 companion object {
 30	 private const val TAG = "SubtitleCache"
 31	 private const val PREFS_NAME = "subtitle_cache"
 32	 private const val KEY_SUBTITLES = "cached_subtitles"
 33	 private const val KEY_MEDIA_MAPPING = "media_subtitle_mapping"
 34	 private const val KEY_LAST_CLEANUP = "last_cleanup"
 35	 private const val SUBTITLE_DIR = "subtitles"
 36	 private const val MAX_CACHE_SIZE = 100 * 1024 * 1024L // 100MB
 37	 private const val CLEANUP_INTERVAL_DAYS = 7
 38	 }
 39	 
 40	 private val preferences: SharedPreferences = context.getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)
 41	 private val cacheMutex = Mutex()
 42	 
 43	 // 内存缓存
 44	 private val subtitleMemoryCache = ConcurrentHashMap<String, Subtitle>()
 45	 private val mediaMappingCache = ConcurrentHashMap<String, MutableSet<String>>()
 46	 
 47	 private val subtitleDir: File by lazy {
 48	 File(context.cacheDir, SUBTITLE_DIR).apply {
 49	 if (!exists()) mkdirs()
 50	 }
 51	 }
 52	 
 53	 init {
 54	 // 启动时加载缓存
 55	 loadCacheFromPreferences()
 56	 
 57	 // 检查是否需要清理
 58	 checkAndCleanupIfNeeded()
 59	 }
 60	 
 61	 /**
 62	 * 保存字幕到缓存
 63	 * @param subtitle 字幕信息
 64	 * @param mediaId 关联的媒体ID（可选）
 65	 */
 66	 suspend fun saveSubtitle(subtitle: Subtitle, mediaId: String? = null) {
 67	 withContext(Dispatchers.IO) {
 68	 cacheMutex.withLock {
 69	 try {
 70	 // 保存到内存缓存
 71	 subtitleMemoryCache[subtitle.id] = subtitle
 72	 
 73	 // 如果有关联媒体，建立映射关系
 74	 mediaId?.let { id ->
 75	 val subtitleIds = mediaMappingCache.getOrPut(id) { mutableSetOf() }
 76	 subtitleIds.add(subtitle.id)
 77	 }
 78	 
 79	 // 持久化到SharedPreferences
 80	 persistCacheToPreferences()
 81	 
 82	 Log.d(TAG, "Subtitle cached: ${subtitle.id} for media: $mediaId")
 83	 
 84	 } catch (e: Exception) {
 85	 Log.e(TAG, "Error saving subtitle to cache", e)
 86	 }
 87	 }
 88	 }
 89	 }
 90	 
 91	 /**
 92	 * 获取媒体相关的字幕
 93	 * @param mediaId 媒体ID
 94	 * @return 字幕列表
 95	 */
 96	 fun getSubtitles(mediaId: String): List<Subtitle> {
 97	 return try {
 98	 val subtitleIds = mediaMappingCache[mediaId] ?: return emptyList()
 99	 subtitleIds.mapNotNull { subtitleMemoryCache[it] }
 100	 .filter { it.isAvailable() } // 只返回可用的字幕
 101	 } catch (e: Exception) {
 102	 Log.e(TAG, "Error getting subtitles for media: $mediaId", e)
 103	 emptyList()
 104	 }
 105	 }
 106	 
 107	 /**
 108	 * 根据ID获取字幕
 109	 * @param subtitleId 字幕ID
 110	 * @return 字幕信息，如果不存在返回null
 111	 */
 112	 fun getSubtitle(subtitleId: String): Subtitle? {
 113	 return subtitleMemoryCache[subtitleId]
 114	 }
 115	 
 116	 /**
 117	 * 搜索字幕
 118	 * @param query 搜索关键词
 119	 * @param language 语言过滤（可选）
 120	 * @return 匹配的字幕列表
 121	 */
 122	 fun searchSubtitles(query: String, language: String? = null): List<Subtitle> {
 123	 return try {
 124	 val lowerQuery = query.lowercase()
 125	 subtitleMemoryCache.values.filter { subtitle ->
 126	 val matchesQuery = subtitle.title.lowercase().contains(lowerQuery) ||
 127	 subtitle.languageName.lowercase().contains(lowerQuery)
 128	 
 129	 val matchesLanguage = language?.let { lang ->
 130	 subtitle.language.equals(lang, ignoreCase = true) ||
 131	 subtitle.language.startsWith(lang, ignoreCase = true)
 132	 } ?: true
 133	 
 134	 matchesQuery && matchesLanguage && subtitle.isAvailable()
 135	 }.sortedByDescending { it.rating }
 136	 } catch (e: Exception) {
 137	 Log.e(TAG, "Error searching subtitles", e)
 138	 emptyList()
 139	 }
 140	 }
 141	 
 142	 /**
 143	 * 删除字幕
 144	 * @param subtitleId 字幕ID
 145	 */
 146	 suspend fun removeSubtitle(subtitleId: String) {
 147	 withContext(Dispatchers.IO) {
 148	 cacheMutex.withLock {
 149	 try {
 150	 val subtitle = subtitleMemoryCache[subtitleId]
 151	 
 152	 // 删除本地文件
 153	 subtitle?.let { sub ->
 154	 if (sub.isDownloaded && sub.localPath.isNotEmpty()) {
 155	 val file = File(sub.localPath)
 156	 if (file.exists() && file.delete()) {
 157	 Log.d(TAG, "Deleted subtitle file: ${sub.localPath}")
 158	 }
 159	 }
 160	 }
 161	 
 162	 // 从内存缓存移除
 163	 subtitleMemoryCache.remove(subtitleId)
 164	 
 165	 // 从媒体映射中移除
 166	 mediaMappingCache.values.forEach { subtitleIds ->
 167	 subtitleIds.remove(subtitleId)
 168	 }
 169	 
 170	 // 持久化更改
 171	 persistCacheToPreferences()
 172	 
 173	 Log.d(TAG, "Subtitle removed from cache: $subtitleId")
 174	 
 175	 } catch (e: Exception) {
 176	 Log.e(TAG, "Error removing subtitle from cache", e)
 177	 }
 178	 }
 179	 }
 180	 }
 181	 
 182	 /**
 183	 * 清理过期字幕
 184	 * @param expireDays 过期天数
 185	 */
 186	 suspend fun cleanExpiredSubtitles(expireDays: Int = 30) {
 187	 withContext(Dispatchers.IO) {
 188	 cacheMutex.withLock {
 189	 try {
 190	 val expireTime = System.currentTimeMillis() - (expireDays * 24 * 60 * 60 * 1000L)
 191	 val expiredSubtitleIds = mutableListOf<String>()
 192	 
 193	 subtitleMemoryCache.values.forEach { subtitle ->
 194	 if (subtitle.uploadDate > 0 && subtitle.uploadDate < expireTime) {
 195	 expiredSubtitleIds.add(subtitle.id)
 196	 }
 197	 }
 198	 
 199	 // 删除过期字幕
 200	 for (subtitleId in expiredSubtitleIds) {
 201	 try {
 202	 val subtitle = subtitleMemoryCache[subtitleId]
 203	 
 204	 // 删除本地文件
 205	 subtitle?.let { sub ->
 206	 if (sub.isDownloaded && sub.localPath.isNotEmpty()) {
 207	 val file = File(sub.localPath)
 208	 if (file.exists() && file.delete()) {
 209	 Log.d(TAG, "Deleted subtitle file: ${sub.localPath}")
 210	 }
 211	 }
 212	 }
 213	 
 214	 // 从内存缓存移除
 215	 subtitleMemoryCache.remove(subtitleId)
 216	 
 217	 // 从媒体映射中移除
 218	 mediaMappingCache.values.forEach { subtitleIds ->
 219	 subtitleIds.remove(subtitleId)
 220	 }
 221	 } catch (e: Exception) {
 222	 Log.e(TAG, "Error removing expired subtitle: $subtitleId", e)
 223	 }
 224	 }
 225	 
 226	 // 持久化更改
 227	 persistCacheToPreferences()
 228	 
 229	 // 清理孤立的字幕文件
 230	 cleanOrphanedFiles()
 231	 
 232	 // 更新最后清理时间
 233	 preferences.edit()
 234	 .putLong(KEY_LAST_CLEANUP, System.currentTimeMillis())
 235	 .apply()
 236	 
 237	 Log.d(TAG, "Cleaned ${expiredSubtitleIds.size} expired subtitles")
 238	 
 239	 } catch (e: Exception) {
 240	 Log.e(TAG, "Error cleaning expired subtitles", e)
 241	 }
 242	 }
 243	 }
 244	 }
 245	 
 246	 /**
 247	 * 清理缓存以释放空间
 248	 * @param targetSize 目标大小（字节）
 249	 */
 250	 suspend fun cleanupToSize(targetSize: Long = MAX_CACHE_SIZE) {
 251	 withContext(Dispatchers.IO) {
 252	 cacheMutex.withLock {
 253	 try {
 254	 val currentSize = getCacheSize()
 255	 if (currentSize <= targetSize) {
 256	 return@withLock
 257	 }
 258	 
 259	 Log.d(TAG, "Cache size: $currentSize bytes, target: $targetSize bytes")
 260	 
 261	 // 按最后访问时间排序，删除最老的文件
 262	 val subtitleFiles = subtitleDir.listFiles()?.sortedBy { it.lastModified() } ?: return@withLock
 263	 var freedSize = 0L
 264	 
 265	 for (file in subtitleFiles) {
 266	 if (currentSize - freedSize <= targetSize) {
 267	 break
 268	 }
 269	 
 270	 val fileSize = file.length()
 271	 if (file.delete()) {
 272	 freedSize += fileSize
 273	 
 274	 // 从缓存中移除对应的字幕记录（非异步方式）
 275	 val subtitleToRemove = subtitleMemoryCache.values.find { 
 276	 it.localPath == file.absolutePath 
 277	 }
 278	 subtitleToRemove?.let { subtitle ->
 279	 // 直接从内存缓存移除，避免suspend函数调用
 280	 subtitleMemoryCache.remove(subtitle.id)
 281	 
 282	 // 从媒体映射中移除
 283	 mediaMappingCache.values.forEach { subtitleIds ->
 284	 subtitleIds.remove(subtitle.id)
 285	 }
 286	 }
 287	 }
 288	 }
 289	 
 290	 Log.d(TAG, "Freed $freedSize bytes from cache")
 291	 
 292	 } catch (e: Exception) {
 293	 Log.e(TAG, "Error cleaning up cache", e)
 294	 }
 295	 }
 296	 }
 297	 }
 298	 
 299	 /**
 300	 * 获取缓存统计信息
 301	 */
 302	 fun getCacheStats(): CacheStats {
 303	 return try {
 304	 val totalSubtitles = subtitleMemoryCache.size
 305	 val downloadedSubtitles = subtitleMemoryCache.values.count { it.isDownloaded }
 306	 val cacheSize = getCacheSize()
 307	 val lastCleanup = preferences.getLong(KEY_LAST_CLEANUP, 0L)
 308	 
 309	 CacheStats(
 310	 totalSubtitles = totalSubtitles,
 311	 downloadedSubtitles = downloadedSubtitles,
 312	 cacheSize = cacheSize,
 313	 lastCleanup = lastCleanup
 314	 )
 315	 } catch (e: Exception) {
 316	 Log.e(TAG, "Error getting cache stats", e)
 317	 CacheStats()
 318	 }
 319	 }
 320	 
 321	 /**
 322	 * 清空所有缓存
 323	 */
 324	 suspend fun clearAllCache() {
 325	 withContext(Dispatchers.IO) {
 326	 cacheMutex.withLock {
 327	 try {
 328	 // 删除所有字幕文件
 329	 subtitleDir.listFiles()?.forEach { file ->
 330	 file.delete()
 331	 }
 332	 
 333	 // 清空内存缓存
 334	 subtitleMemoryCache.clear()
 335	 mediaMappingCache.clear()
 336	 
 337	 // 清空持久化数据
 338	 preferences.edit().clear().apply()
 339	 
 340	 Log.d(TAG, "All cache cleared")
 341	 
 342	 } catch (e: Exception) {
 343	 Log.e(TAG, "Error clearing cache", e)
 344	 }
 345	 }
 346	 }
 347	 }
 348	 
 349	 // 私有辅助方法
 350	 
 351	 private fun loadCacheFromPreferences() {
 352	 try {
 353	 // 加载字幕数据
 354	 val subtitlesJson = preferences.getString(KEY_SUBTITLES, "")
 355	 if (!subtitlesJson.isNullOrEmpty()) {
 356	 val type = object : TypeToken<Map<String, Subtitle>>() {}.type
 357	 val subtitles: Map<String, Subtitle> = gson.fromJson(subtitlesJson, type)
 358	 subtitleMemoryCache.putAll(subtitles)
 359	 }
 360	 
 361	 // 加载媒体映射
 362	 val mappingJson = preferences.getString(KEY_MEDIA_MAPPING, "")
 363	 if (!mappingJson.isNullOrEmpty()) {
 364	 val type = object : TypeToken<Map<String, Set<String>>>() {}.type
 365	 val mapping: Map<String, Set<String>> = gson.fromJson(mappingJson, type)
 366	 mapping.forEach { (mediaId, subtitleIds) ->
 367	 mediaMappingCache[mediaId] = subtitleIds.toMutableSet()
 368	 }
 369	 }
 370	 
 371	 Log.d(TAG, "Loaded ${subtitleMemoryCache.size} subtitles from cache")
 372	 
 373	 } catch (e: Exception) {
 374	 Log.e(TAG, "Error loading cache from preferences", e)
 375	 }
 376	 }
 377	 
 378	 private fun persistCacheToPreferences() {
 379	 try {
 380	 val editor = preferences.edit()
 381	 
 382	 // 保存字幕数据
 383	 val subtitlesJson = gson.toJson(subtitleMemoryCache)
 384	 editor.putString(KEY_SUBTITLES, subtitlesJson)
 385	 
 386	 // 保存媒体映射
 387	 val mappingJson = gson.toJson(mediaMappingCache)
 388	 editor.putString(KEY_MEDIA_MAPPING, mappingJson)
 389	 
 390	 editor.apply()
 391	 
 392	 } catch (e: Exception) {
 393	 Log.e(TAG, "Error persisting cache to preferences", e)
 394	 }
 395	 }
 396	 
 397	 private fun getCacheSize(): Long {
 398	 return try {
 399	 subtitleDir.listFiles()?.sumOf { it.length() } ?: 0L
 400	 } catch (e: Exception) {
 401	 0L
 402	 }
 403	 }
 404	 
 405	 private fun cleanOrphanedFiles() {
 406	 try {
 407	 val cachedPaths = subtitleMemoryCache.values
 408	 .mapNotNull { it.localPath.takeIf { path -> path.isNotEmpty() } }
 409	 .toSet()
 410	 
 411	 subtitleDir.listFiles()?.forEach { file ->
 412	 if (file.absolutePath !in cachedPaths) {
 413	 if (file.delete()) {
 414	 Log.d(TAG, "Deleted orphaned file: ${file.name}")
 415	 }
 416	 }
 417	 }
 418	 } catch (e: Exception) {
 419	 Log.w(TAG, "Error cleaning orphaned files: ${e.message}")
 420	 }
 421	 }
 422	 
 423	 private fun checkAndCleanupIfNeeded() {
 424	 try {
 425	 val lastCleanup = preferences.getLong(KEY_LAST_CLEANUP, 0L)
 426	 val daysSinceCleanup = (System.currentTimeMillis() - lastCleanup) / (24 * 60 * 60 * 1000L)
 427	 
 428	 if (daysSinceCleanup >= CLEANUP_INTERVAL_DAYS) {
 429	 // 异步执行清理，但不等待结果以避免suspend函数调用错误
 430	 kotlinx.coroutines.GlobalScope.launch {
 431	 try {
 432	 cleanExpiredSubtitles()
 433	 cleanupToSize()
 434	 } catch (e: Exception) {
 435	 Log.w(TAG, "Error during background cleanup: ${e.message}")
 436	 }
 437	 }
 438	 }
 439	 } catch (e: Exception) {
 440	 Log.w(TAG, "Error checking cleanup: ${e.message}")
 441	 }
 442	 }
 443	}
