 455	 get() = cacheSize / (1024f * 1024f)
 456	 
 457	 val daysSinceCleanup: Long
 458	 get() = if (lastCleanup > 0) {
 459	 (System.currentTimeMillis() - lastCleanup) / (24 * 60 * 60 * 1000L)
 460	 } else {
 461	 -1L
 462	 }
