 51	 val shutdownMethod = proxyCacheManagerClass.getMethod("shutdown")
 52	 shutdownMethod.invoke(null)
 53	 Log.i("TVPlayerApplication", "ProxyCache disabled for WebDAV support")
 54	 }
 55	 } catch (e: Exception) {
 56	 Log.w("TVPlayerApplication", "Could not disable ProxyCache, using alternative method: ${e.message}")
 57	 
 58	 // 替代方法：设置空的缓存目录
 59	 try {
 60	 System.setProperty("http.proxyHost", "")
 61	 System.setProperty("http.proxyPort", "")
 62	 System.setProperty("https.proxyHost", "")
 63	 System.setProperty("https.proxyPort", "")
 64	 Log.i("TVPlayerApplication", "Proxy settings cleared for WebDAV support")
 65	 } catch (ex: Exception) {
 66	 Log.e("TVPlayerApplication", "Failed to configure proxy settings: ${ex.message}")
 67	 }
