 10	import kotlinx.coroutines.Dispatchers
 11	import kotlinx.coroutines.sync.Mutex
 12	import kotlinx.coroutines.sync.withLock
 13	import kotlinx.coroutines.withContext
 14	import java.io.File
 15	import java.util.concurrent.ConcurrentHashMap
 16	import javax.inject.Inject
 17	import javax.inject.Singleton
 18	
 19	/**
 20	 * 字幕缓存管理器
 21	 * 负责字幕信息的本地存储、检索和清理
 22	 */
 23	@Singleton
 24	class SubtitleCache @Inject constructor(
 25	 @ApplicationContext private val context: Context,
 26	 private val gson: Gson
 27	) {
 28	 
 29	 companion object {
 30	 private const val TAG = "SubtitleCache"
 31	 private const val PREFS_NAME = "subtitle_cache"
 32	 private const val KEY_SUBTITLES = "cached_subtitles"
 33	 private const val KEY_MEDIA_MAPPING = "media_subtitle_mapping"
 34	 private const val KEY_LAST_CLEANUP = "last_cleanup"
 35	 private const val SUBTITLE_DIR = "subtitles"
 36	 private const val MAX_CACHE_SIZE = 100 * 1024 * 1024L // 100MB
 37	 private const val CLEANUP_INTERVAL_DAYS = 7
 38	 }
 39	 
 40	 private val preferences: SharedPreferences = context.getSharedPreferences(PREFS_NAME, Context.MODE_PRIVATE)
 41	 private val cacheMutex = Mutex()
 42	 
 43	 // 内存缓存
 44	 private val subtitleMemoryCache = ConcurrentHashMap<String, Subtitle>()
 45	 private val mediaMappingCache = ConcurrentHashMap<String, MutableSet<String>>()
 46	 
 47	 private val subtitleDir: File by lazy {
 48	 File(context.cacheDir, SUBTITLE_DIR).apply {
 49	 if (!exists()) mkdirs()
 50	 }
 51	 }
 52	 
 53	 init {
 54	 // 启动时加载缓存
 55	 loadCacheFromPreferences()
 56	 
 57	 // 检查是否需要清理
 58	 checkAndCleanupIfNeeded()
 59	 }
 60	 
 61	 /**
 62	 * 保存字幕到缓存
 63	 * @param subtitle 字幕信息
 64	 * @param mediaId 关联的媒体ID（可选）
 65	 */
 66	 suspend fun saveSubtitle(subtitle: Subtitle, mediaId: String? = null) {
 67	 withContext(Dispatchers.IO) {
 68	 cacheMutex.withLock {
 69	 try {
 70	 // 保存到内存缓存
 71	 subtitleMemoryCache[subtitle.id] = subtitle
 72	 
 73	 // 如果有关联媒体，建立映射关系
 74	 mediaId?.let { id ->
 75	 val subtitleIds = mediaMappingCache.getOrPut(id) { mutableSetOf() }
 76	 subtitleIds.add(subtitle.id)
 77	 }
 78	 
 79	 // 持久化到SharedPreferences
 80	 persistCacheToPreferences()
 81	 
 82	 Log.d(TAG, "Subtitle cached: ${subtitle.id} for media: $mediaId")
 83	 
 84	 } catch (e: Exception) {
 85	 Log.e(TAG, "Error saving subtitle to cache", e)
 86	 }
 87	 }
 88	 }
 89	 }
...
 422	 
 423	 private fun checkAndCleanupIfNeeded() {
 424	 try {
 425	 val lastCleanup = preferences.getLong(KEY_LAST_CLEANUP, 0L)
 426	 val daysSinceCleanup = (System.currentTimeMillis() - lastCleanup) / (24 * 60 * 60 * 1000L)
 427	 
 428	 if (daysSinceCleanup >= CLEANUP_INTERVAL_DAYS) {
 429	 // 异步执行清理，但不等待结果以避免suspend函数调用错误
 430	 kotlinx.coroutines.GlobalScope.launch {
 431	 try {
 432	 cleanExpiredSubtitles()
 433	 cleanupToSize()
 434	 } catch (e: Exception) {
 435	 Log.w(TAG, "Error during background cleanup: ${e.message}")
 436	 }
 437	 }
 438	 }
 439	 } catch (e: Exception) {
 440	 Log.w(TAG, "Error checking cleanup: ${e.message}")
 441	 }
 442	 }
 443	}
