 325	 /**
 326	 * 计算质量分数
 327	 */
 328	 private fun calculateQualityScore(subtitle: Subtitle): Float {
 329	 var score = 0.0f
 330	 
 331	 // 评分权重
 332	 if (subtitle.rating > 0) {
 333	 score += (subtitle.rating / 10.0f) * 0.4f
 334	 }
 335	 
 336	 // 下载次数权重
 337	 if (subtitle.downloadCount > 0) {
 338	 val downloadScore = min(1.0f, subtitle.downloadCount / 1000.0f)
 339	 score += downloadScore * 0.3f
 340	 }
 341	 
 342	 // 来源权重
 343	 score += when (subtitle.source) {
 344	 SubtitleSource.OPENSUBTITLES -> 0.3f
 345	 SubtitleSource.SUBSCENE -> 0.25f
 346	 SubtitleSource.LOCAL -> 0.2f
 347	 else -> 0.1f
 348	 }
 349	 
 350	 return min(1.0f, score)
 351	 }
