 44	 /**
 45	 * 获取显示标题
 46	 */
 47	 fun getDisplayTitle(): String {
 48	 fun deriveNameFromFile(): String {
 49	 val base = filePath.substringAfterLast('/')
 50	 .substringBeforeLast('.')
 51	 .replace(Regex("[\\[\\(（【].*?[\\]\\)）】]"), " ")
 52	 .replace(Regex("[._-]+"), " ")
 53	 .replace(Regex("\\s+"), " ")
 54	 .trim()
 55	 return if (base.isNotBlank()) base else "未命名"
 56	 }
 57	 return when (mediaType) {
 58	 MediaType.TV_EPISODE -> {
 59	 val composite = if (seasonNumber != null && episodeNumber != null && !seriesTitle.isNullOrBlank()) {
 60	 "$seriesTitle S${seasonNumber.toString().padStart(2, '0')}E${episodeNumber.toString().padStart(2, '0')}"
 61	 } else title
 62	 (composite?.takeIf { it.isNotBlank() } ?: seriesTitle ?: deriveNameFromFile())
 63	 }
 64	 else -> (title.takeIf { it.isNotBlank() } ?: originalTitle?.takeIf { it.isNotBlank() } ?: deriveNameFromFile())
 65	 }
 66	 }
 67	 
 68	 /**
 69	 * 获取副标题
 70	 */
 71	 fun getSubtitle(): String? {
 72	 return when (mediaType) {
 73	 MediaType.TV_EPISODE -> title.takeIf { it != getDisplayTitle() }
 74	 MediaType.MOVIE -> {
 75	 val dateStr = releaseDate?.let {
 76	 java.text.SimpleDateFormat("yyyy-MM-dd", java.util.Locale.getDefault()).format(it)
 77	 }
 78	 val durationStr = if (duration > 0) "${duration / 60}分钟" else null
 79	 listOfNotNull(dateStr, durationStr).joinToString(" • ")
 80	 }
 81	 else -> null
 82	 }
 83	 }
 84	 
 85	 /**
 86	 * 获取格式化的文件大小
 87	 */
 88	 fun getFormattedFileSize(): String {
 89	 return when {
 90	 fileSize < 1024 -> "${fileSize}B"
 91	 fileSize < 1024 * 1024 -> "${fileSize / 1024}KB"
 92	 fileSize < 1024 * 1024 * 1024 -> "${fileSize / (1024 * 1024)}MB"
 93	 else -> String.format("%.1f GB", fileSize / (1024.0 * 1024.0 * 1024.0))
 94	 }
 95	 }
