 404	 return try {
 405	 val mimeType = when (format) {
 406	 SubtitleFormat.SUB -> MimeTypes.APPLICATION_DVBSUBS
 407	 SubtitleFormat.SMI -> "application/x-sami"
 408	 SubtitleFormat.TXT -> MimeTypes.TEXT_PLAIN
 409	 else -> MimeTypes.APPLICATION_SUBRIP
 410	 }
 411	 
 412	 videoPlayer?.let { player ->
 413	 val uri = Uri.fromFile(file)
 414	 setupExoPlayerSubtitle(player.context, uri, mimeType)
 415	 } ?: false
 416	 } catch (e: Exception) {
 417	 Log.e(TAG, "Error loading generic subtitle", e)
 418	 false
 419	 }
