 54	 @Singleton
 55	 @TmdbClient
 56	 fun provideTmdbOkHttpClient(): OkHttpClient {
 57	 val loggingInterceptor = HttpLoggingInterceptor().apply {
 58	 level = HttpLoggingInterceptor.Level.BASIC
 59	 }
 60	
 61	 return OkHttpClient.Builder()
 62	 .addInterceptor(loggingInterceptor)
 63	 .connectTimeout(15, TimeUnit.SECONDS)
 64	 .readTimeout(15, TimeUnit.SECONDS)
 65	 .writeTimeout(15, TimeUnit.SECONDS)
 66	 // SSL配置：重试连接
 67	 .retryOnConnectionFailure(true)
 68	 // 添加SSL配置以解决证书验证问题
 69	 .apply { configureSSL(this) }
 70	 .build()
 71	 }
 72	
 73	 /**
 74	 * 配置SSL以解决证书验证问题
 75	 * ⚠️ 注意：此配置仅用于开发环境，生产环境应使用正确的证书验证
 76	 */
 77	 private fun configureSSL(builder: OkHttpClient.Builder) {
 78	 try {
 79	 // 创建信任所有证书的TrustManager（仅用于开发）
 80	 val trustAllCerts = arrayOf<TrustManager>(object : X509TrustManager {
 81	 override fun checkClientTrusted(chain: Array<X509Certificate>, authType: String) {}
 82	 override fun checkServerTrusted(chain: Array<X509Certificate>, authType: String) {}
 83	 override fun getAcceptedIssuers(): Array<X509Certificate> = arrayOf()
 84	 })
 85	
 86	 // 安装信任所有证书的SSLContext
 87	 val sslContext = SSLContext.getInstance("SSL")
 88	 sslContext.init(null, trustAllCerts, SecureRandom())
 89	 
 90	 builder.sslSocketFactory(sslContext.socketFactory, trustAllCerts[0] as X509TrustManager)
 91	 builder.hostnameVerifier { _, _ -> true }
 92	 } catch (e: Exception) {
 93	 // 如果SSL配置失败，记录错误但不影响应用启动
 94	 e.printStackTrace()
 95	 }
 96	 }
