 79	 private fun groupTVEpisodesBySeries(items: List<MediaItem>): List<TVSeriesSummary> {
 80	 val tvEpisodes = items.filter {
 81	 it.mediaType == MediaType.TV_EPISODE || it.mediaType == MediaType.TV_SERIES
 82	 }
 83	
 84	 val groupedBySeries = tvEpisodes.groupBy { episode ->
 85	 episode.seriesId ?: episode.seriesTitle ?: episode.title
 86	 }
 87	
 88	 return groupedBySeries.map { (seriesKey, episodes) ->
 89	 val firstEpisode = episodes.first()
 90	 val seasons = episodes.mapNotNull { it.seasonNumber }.distinct()
 91	 val totalSeasons = seasons.size
 92	 val totalEpisodes = episodes.size
 93	 val watchedEpisodes = episodes.count { it.watchedProgress > 0.9f } // 90%以上算看完
 94	 val lastWatchedTime = episodes.mapNotNull { it.lastWatchedTime }.maxOrNull()
 95	
 96	 TVSeriesSummary(
 97	 seriesId = firstEpisode.seriesId ?: "series_${seriesKey.hashCode()}",
