 86	 persistCacheToPreferences()
 87	 
 88	 Log.d(TAG, "Subtitle cached: ${subtitle.id} for media: $mediaId")
 89	 
 90	 } catch (e: Exception) {
 91	 Log.e(TAG, "Error saving subtitle to cache", e)
 92	 }
 93	 }
 94	 }
