 197	 /**
 198	 * 获取媒体的所有可用字幕
 199	 * @param mediaItem 媒体项
 200	 * @param config 字幕配置
 201	 * @return 可用字幕列表
 202	 */
 203	 suspend fun getAvailableSubtitles(
 204	 mediaItem: MediaItem,
 205	 config: SubtitleConfig
 206	 ): List<Subtitle> {
 207	 return withContext(Dispatchers.IO) {
 208	 try {
 209	 // 先查找本地字幕
 210	 val localSubtitles = findLocalSubtitles(mediaItem)
 211	 
 212	 // 查找缓存的字幕
 213	 val cachedSubtitles = getCachedSubtitles(
 214	 SubtitleSearchRequest.fromMediaItem(mediaItem, config)
 215	 ).filter { it.isDownloaded && it.isAvailable() }
 216	 
 217	 // 合并并去重
 218	 val allSubtitles = (localSubtitles + cachedSubtitles)
 219	 .distinctBy { "${it.language}_${it.source}_${it.hash}" }
 220	 
 221	 Log.d(TAG, "Found ${allSubtitles.size} available subtitles for ${mediaItem.title}")
 222	 allSubtitles
 223	 
 224	 } catch (e: Exception) {
 225	 Log.e(TAG, "Error getting available subtitles", e)
 226	 emptyList()
 227	 }
 228	 }
 229	 }
 230	 
 231	 /**
 232	 * 自动选择最佳字幕
 233	 * @param mediaItem 媒体项
 234	 * @param config 字幕配置
 235	 * @return 推荐的字幕，如果没有合适的返回null
 236	 */
 237	 suspend fun selectBestSubtitle(
 238	 mediaItem: MediaItem,
 239	 config: SubtitleConfig
 240	 ): Subtitle? {
 241	 return withContext(Dispatchers.IO) {
 242	 try {
 243	 val availableSubtitles = getAvailableSubtitles(mediaItem, config)
 244	 
 245	 if (availableSubtitles.isEmpty()) {
 246	 // 如果没有可用字幕，自动搜索
 247	 if (config.autoDownload) {
 248	 val searchRequest = SubtitleSearchRequest.fromMediaItem(mediaItem, config)
 249	 val searchResult = searchSubtitles(searchRequest, config)
 250	 
 251	 if (searchResult.hasResults) {
 252	 val bestSubtitle = searchResult.subtitles.first()
 253	 val downloadResult = downloadSubtitle(bestSubtitle, mediaItem.id)
 254	 
 255	 return@withContext downloadResult.getOrNull()
 256	 }
 257	 }
 258	 return@withContext null
 259	 }
 260	 
 261	 // 根据配置选择最佳字幕
 262	 selectBestFromAvailable(availableSubtitles, config)
 263	 
 264	 } catch (e: Exception) {
 265	 Log.e(TAG, "Error selecting best subtitle", e)
 266	 null
 267	 }
 268	 }
 269	 }
