 283	 if (file.delete()) {
 284	 Log.d(TAG, "Deleted expired subtitle: ${file.name}")
 285	 }
 286	 }
 287	 
 288	 // 清理缓存数据库中的过期记录
 289	 subtitleCache.cleanExpiredSubtitles(30)
 290	 
 291	 } catch (e: Exception) {
 292	 Log.e(TAG, "Error cleaning cache", e)
 293	 }
 294	 }
