 6	
 7	/**
 8	 * 字幕文件数据模型
 9	 */
 10	@Parcelize
 11	data class Subtitle(
 12	 val id: String = "", // 字幕唯一标识
 13	 val title: String = "", // 字幕标题
 14	 val language: String = "", // 语言代码 (zh, en, zh-cn, en-us)
 15	 val languageName: String = "", // 语言显示名称
 16	 val format: SubtitleFormat = SubtitleFormat.SRT, // 字幕格式
 17	 val encoding: String = "UTF-8", // 字幕编码
 18	 val downloadUrl: String = "", // 下载链接
 19	 val localPath: String = "", // 本地文件路径
 20	 val fileSize: Long = 0L, // 文件大小（字节）
 21	 val hash: String = "", // 文件哈希值
 22	 val source: SubtitleSource = SubtitleSource.UNKNOWN, // 字幕来源
 23	 val rating: Float = 0.0f, // 评分 (0.0-10.0)
 24	 val downloadCount: Int = 0, // 下载次数
 25	 val uploadDate: Long = 0L, // 上传时间戳
 26	 val uploader: String = "", // 上传者
 27	 val isDownloaded: Boolean = false, // 是否已下载
 28	 val syncInfo: SubtitleSyncInfo? = null, // 同步信息
 29	 val metadata: Map<String, String> = emptyMap() // 额外元数据
 30	) : Parcelable {
 31	 
 32	 /**
 33	 * 获取本地字幕文件
 34	 */
 35	 fun getLocalFile(): File? {
 36	 return if (isDownloaded && localPath.isNotEmpty()) {
 37	 File(localPath).takeIf { it.exists() }
 38	 } else null
 39	 }
 40	 
 41	 /**
 42	 * 检查字幕是否可用
 43	 */
 44	 fun isAvailable(): Boolean {
 45	 return isDownloaded && getLocalFile() != null
 46	 }
 47	 
 48	 /**
 49	 * 获取字幕描述信息
 50	 */
 51	 fun getDescription(): String {
 52	 val parts = mutableListOf<String>()
 53	 if (languageName.isNotEmpty()) parts.add(languageName)
 54	 if (rating > 0) parts.add("评分: %.1f".format(rating))
 55	 if (downloadCount > 0) parts.add("下载: $downloadCount")
 56	 return parts.joinToString(" | ")
 57	 }
 58	 
 59	 /**
 60	 * 获取显示标题
 61	 */
 62	 fun getDisplayTitle(): String {
 63	 return if (title.isNotEmpty()) title else "字幕 - $languageName"
 64	 }
 65	}
 66	
 67	/**
 68	 * 字幕格式枚举
 69	 */
 70	@Parcelize
 71	enum class SubtitleFormat(val extension: String, val mimeType: String) : Parcelable {
 72	 SRT("srt", "application/x-subrip"),
 73	 ASS("ass", "text/x-ssa"),
 74	 SSA("ssa", "text/x-ssa"),
 75	 VTT("vtt", "text/vtt"),
 76	 SUB("sub", "text/x-subviewer"),
 77	 IDX("idx", "application/x-subtitle"),
 78	 SMI("smi", "application/x-sami"),
 79	 TXT("txt", "text/plain");
 80	 
 81	 companion object {
 82	 fun fromExtension(extension: String): SubtitleFormat {
 83	 return values().find { 
 84	 it.extension.equals(extension, ignoreCase = true) 
 85	 } ?: SRT
 86	 }
 87	 
 88	 fun fromFileName(fileName: String): SubtitleFormat {
 89	 val ext = fileName.substringAfterLast('.', "")
 90	 return fromExtension(ext)
 91	 }
 92	 }
 93	}
