 10	@Parcelize
 11	data class SubtitleConfig(
 12	 // 显示设置
 13	 val isEnabled: Boolean = true, // 是否启用字幕
 14	 val primaryLanguage: String = "zh", // 首选语言
 15	 val fallbackLanguage: String = "en", // 备用语言
 16	 val autoSelectLanguage: Boolean = true, // 自动选择语言
 17	 
 18	 // 样式设置
 19	 val textSize: Float = 16.0f, // 字体大小 (sp)
 20	 val textColor: Int = Color.WHITE, // 字体颜色
 21	 val backgroundColor: Int = Color.TRANSPARENT, // 背景颜色
 22	 val outlineColor: Int = Color.BLACK, // 描边颜色
 23	 val outlineWidth: Float = 1.0f, // 描边宽度
 24	 val shadowColor: Int = Color.BLACK, // 阴影颜色
 25	 val shadowRadius: Float = 1.0f, // 阴影半径
 26	 val shadowOffsetX: Float = 1.0f, // 阴影X偏移
 27	 val shadowOffsetY: Float = 1.0f, // 阴影Y偏移
 28	 
 29	 // 位置设置
 30	 val position: SubtitlePosition = SubtitlePosition.BOTTOM, // 字幕位置
 31	 val marginHorizontal: Float = 32.0f, // 水平边距 (dp)
 32	 val marginVertical: Float = 48.0f, // 垂直边距 (dp)
 33	 val alignment: SubtitleAlignment = SubtitleAlignment.CENTER, // 对齐方式
 34	 
 35	 // 字体设置
 36	 val fontFamily: String = "default", // 字体族
 37	 val isBold: Boolean = false, // 是否粗体
 38	 val isItalic: Boolean = false, // 是否斜体
 39	 
 40	 // 同步设置
 41	 val globalOffsetMs: Long = 0L, // 全局时间偏移（毫秒）
 42	 val autoSync: Boolean = true, // 自动同步
 43	 val syncSensitivity: Float = 0.5f, // 同步敏感度 (0.0-1.0)
 44	 
 45	 // 下载设置
 46	 val autoDownload: Boolean = true, // 自动下载字幕
 47	 val downloadQuality: SubtitleQuality = SubtitleQuality.BEST, // 下载质量偏好
 48	 val maxCacheSize: Long = 100 * 1024 * 1024L, // 最大缓存大小 (100MB)
 49	 val cacheExpireDays: Int = 30, // 缓存过期天数
 50	 
 51	 // 搜索设置
 52	 val enabledSources: Set<SubtitleSource> = setOf(
 53	 SubtitleSource.OPENSUBTITLES,
 54	 SubtitleSource.SUBSCENE
 55	 ), // 启用的字幕源
 56	 val searchTimeout: Int = 10, // 搜索超时时间（秒）
 57	 val maxResults: Int = 20, // 最大搜索结果数
 58	 
 59	 // 高级设置
 60	 val encoding: String = "UTF-8", // 默认编码
 61	 val lineSpacing: Float = 1.2f, // 行间距
 62	 val maxLines: Int = 3, // 最大行数
 63	 val wordWrap: Boolean = true, // 自动换行
 64	 val fadeInDuration: Long = 200L, // 淡入时长（毫秒）
 65	 val fadeOutDuration: Long = 200L // 淡出时长（毫秒）
 66	) : Parcelable {
 67	 
