 115	@Parcelize
 116	data class SubtitleSyncInfo(
 117	 val offsetMs: Long = 0L, // 时间偏移（毫秒）
 118	 val speedRatio: Float = 1.0f, // 播放速度比例
 119	 val fps: Float = 0.0f, // 帧率
 120	 val isAutoSynced: Boolean = false, // 是否自动同步
 121	 val confidence: Float = 0.0f // 同步置信度 (0.0-1.0)
 122	) : Parcelable {
 123	 
 124	 /**
 125	 * 应用时间偏移
 126	 */
 127	 fun applyOffset(originalTimeMs: Long): Long {
 128	 return ((originalTimeMs * speedRatio) + offsetMs).toLong()
 129	 }
