 171	 /**
 172	 * 为WebDAV播放配置特殊选项
 173	 */
 174	 private fun configureForWebDAV(uri: Uri, username: String?, password: String?) {
 175	 try {
 176	 // 检查是否为WebDAV URL
 177	 if (isWebDAVUrl(uri)) {
 178	 Log.d("PlayerActivity", "Configuring for WebDAV playback")
 179	 
 180	 // 禁用全局缓存服务器
 181	 try {
 182	 val proxyCacheManagerClass = Class.forName("com.danikula.videocache.ProxyCacheManager")
 183	 val shutdownMethod = proxyCacheManagerClass.getMethod("shutdown")
 184	 shutdownMethod.invoke(null)
 185	 Log.d("PlayerActivity", "ProxyCache disabled for WebDAV")
 186	 } catch (e: Exception) {
 187	 Log.w("PlayerActivity", "Could not disable ProxyCache: ${e.message}")
 188	 }
 189	 
 190	 // 设置HTTP头部，包括认证信息
 191	 val headers = hashMapOf<String, String>(
 192	 "Accept-Ranges" to "bytes",
 193	 "Connection" to "keep-alive",
 194	 "User-Agent" to "AndroidTVPlayer/1.0",
 195	 "Accept" to "*/*"
 196	 )
 197	 
 198	 // 如果有认证信息，添加Authorization头部
 199	 if (!username.isNullOrEmpty() && !password.isNullOrEmpty()) {
 200	 val credentials = android.util.Base64.encodeToString(
 201	 "$username:$password".toByteArray(),
 202	 android.util.Base64.NO_WRAP
 203	 )
 204	 headers["Authorization"] = "Basic $credentials"
 205	 Log.d("PlayerActivity", "Added Basic Authentication header")
 206	 }
 207	 
 208	 videoPlayer.setMapHeadData(headers)
 209	 
 210	 Log.d("PlayerActivity", "WebDAV headers configured: ${headers.keys}")
 211	 }
 212	 } catch (e: Exception) {
 213	 Log.e("PlayerActivity", "Error configuring WebDAV: ${e.message}", e)
 214	 }
 215	 }
 216	 
 217	 /**
 218	 * 检查是否为WebDAV URL
 219	 */
 220	 private fun isWebDAVUrl(uri: Uri): Boolean {
 221	 val scheme = uri.scheme?.lowercase()
 222	 return (scheme == "http" || scheme == "https") && 
 223	 uri.host != null && 
 224	 uri.host != "localhost" && 
 225	 uri.host != "127.0.0.1"
 226	 }
