 388	 /**
 389	 * 从 Intent 创建 MediaItem
 390	 */
 391	 private fun createMediaItemFromIntent(): MediaItem? {
 392	 return try {
 393	 val title = mediaTitle ?: return null
 394	 val uriString = intent.getStringExtra(EXTRA_URI) ?: return null
 395	 val uri = Uri.parse(uriString)
 396	 
 397	 // 从文件名提取信息
 398	 val fileName = uri.lastPathSegment ?: title
 399	 val year = extractYearFromTitle(title)
 400	 val (season, episode) = extractSeasonEpisodeFromTitle(title)
 401	 
 402	 MediaItem(
 403	 id = mediaId ?: title.hashCode().toString(),
 404	 title = title,
 405	 filePath = uriString,
 406	 mediaType = if (season > 0) com.tvplayer.webdav.data.model.MediaType.TV_EPISODE else com.tvplayer.webdav.data.model.MediaType.MOVIE,
 407	 seasonNumber = season.takeIf { it > 0 },
 408	 episodeNumber = episode.takeIf { it > 0 },
 409	 fileSize = 0L, // 无法获取文件大小
 410	 duration = 0L, // 无法获取时长
 411	 releaseDate = if (year > 0) {
 412	 val calendar = java.util.Calendar.getInstance()
 413	 calendar.set(year, 0, 1)
 414	 calendar.time
 415	 } else null
 416	 )
 417	 } catch (e: Exception) {
 418	 Log.e("PlayerActivity", "Error creating MediaItem from intent", e)
 419	 null
 420	 }
 421	 }
 422	 
 423	 /**
 424	 * 从标题提取年份
 425	 */
 426	 private fun extractYearFromTitle(title: String): Int {
 427	 val yearRegex = """(\d{4})""".toRegex()
 428	 return yearRegex.find(title)?.value?.toIntOrNull() ?: 0
 429	 }
